package com.example.itc_recycle_view;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.GridLayoutManager;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.os.Bundle;import java.util.ArrayList;public class MainActivity extends AppCompatActivity {    private ArrayList<Movie> listMovie;    private RecyclerView recyclerView;    private MovieListAdapter listAdapter;    private MovieGridAdapter gridAdapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        recyclerView = findViewById(R.id.rv_item);        listMovie = DataMovie.getAllDataMovie();        recyclerView.setHasFixedSize(true);        setLinierList();    }    void setLinierList(){        listAdapter = new MovieListAdapter(listMovie);        recyclerView.setLayoutManager(new LinearLayoutManager(this));        recyclerView.setAdapter(listAdapter);    }    void setGridLinier(){        gridAdapter = new MovieGridAdapter(listMovie);        recyclerView.setLayoutManager(new GridLayoutManager(this,2));        recyclerView.setAdapter(gridAdapter);    }}